mapper.insert:
INSERT
INTO
course(
id
,name
,alias
,fee
,lesson1
,lesson2
,lesson3
,lesson4
)
VALUES
(
#{ id }
,#{ name }
,#{ alias }
,#{ fee }
,#{ lesson1 }
,#{ lesson2 }
,#{ lesson3 }
,#{ lesson4 }
)

mapper.update:
UPDATE
course
SET
name = #{ name }
,alias = #{ alias }
,fee =#{ fee }
,lesson1 =#{ lesson1 }
,lesson2 =#{ lesson2 }
,lesson3 =#{ lesson3 }
,lesson4 =#{ lesson4 }
WHERE
id =#{ id }
