mapper.insert:
INSERT
INTO
student
VALUES
(
#{ id }
,#{ name }
,#{ kana }
,#{ gender }
,#{ grade }
,#{ birthday }
,#{ school }
,#{ parentsName }
,#{ mailAddress }
,#{ phoneNumber }
,#{ zipcode }
,#{ address }
,#{ course }
,current_timestamp
,null
)

mapper.update:
UPDATE
student
SET
name = #{ name }
,kana = #{ kana }
,gender = #{ gender }
,grade = #{ grade }
,birthday = #{ birthday }
,school = #{ school }
,parents_name = #{ parentsName }
,phone_number = #{ phoneNumber }
,mail_address = #{ mailAddress }
,zipcode = #{ zipcode }
,address = #{ address }
,course = #{ course }
,update_datetime = CURRENT_TIMESTAMP
WHERE
id =#{ id }
